"use client";
import * as React from "react";
import { cn } from "@/lib/utils";
/* --------------------
   Styles
-------------------- */
const baseClasses = "inline-flex items-center justify-center rounded-md border px-2 py-0.5 text-xs font-medium w-fit whitespace-nowrap shrink-0 gap-1 transition-colors";
/* Update colors/styles here */
const variantClasses = {
    default: "bg-[var(--foreground)] text-[var(--background)] border-transparent",
    secondary: "bg-[var(--info)] text-white border-transparent",
    destructive: "bg-destructive text-white border-transparent",
    outline: "bg-transparent text-[var(--foreground)] border-[var(--border)] hover:bg-[var(--foreground)/0.05]",
    notification: "bg-[var(--info)] text-white border-transparent rounded-full w-5 aspect-square text-[10px] flex items-center justify-center p-0 leading-none text-center overflow-hidden",
};
/* --------------------
   Badge Component
-------------------- */
function Badge({ variant = "default", asChild = false, className, children, ...props }) {
    const Component = asChild ? React.Fragment : "span";
    const classes = variant === "notification"
        ? cn(variantClasses[variant], className)
        : cn(baseClasses, variantClasses[variant], className);
    return (React.createElement(Component, { className: classes, ...props }, children));
}
/* --------------------
   Export
-------------------- */
export { Badge };
