"use client";

/* -------------------- Imports -------------------- */
// External libs, UI components, and utilities
import * as React from "react";
import {
  Select,
  SelectTrigger,
  SelectContent,
  SelectItem,
  SelectValue,
} from "./select";
import { Input } from "./input";
import { cn } from "@/lib/utils";
import { SearchIcon } from "lucide-react";
import { Separator } from "./separator";

/* -------------------- Types -------------------- */
// Props definition for the ComboBox component
type ComboBoxProps = {
  items: string[];
  value: string;
  onValueChange: (value: string) => void;
  placeholder?: string;
  errorMessage?: string;
  noMatchFallback?: string;
  className?: string;
};

/* -------------------- Component -------------------- */
// ComboBox with searchable dropdown functionality
function ComboBox({
  items,
  value,
  onValueChange,
  placeholder = "Select an item",
  errorMessage,
  noMatchFallback = "No such item found",
  className,
}: ComboBoxProps) {
  const [query, setQuery] = React.useState("");

  const filteredItems = items.filter((item) =>
    item.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <div className={cn("space-y-1", className)}>
      <Select value={value} onValueChange={onValueChange}>
        <SelectTrigger>
          <SelectValue placeholder={placeholder} />
        </SelectTrigger>
        <SelectContent className="pt-0">
          {/* Search bar */}
          <div className="py-2 sticky top-0 z-[1] bg-background">
            <Input
              icon={<SearchIcon className="w-4 h-4" />}
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Search..."
              className="h-8 text-sm"
            />
          </div>

          <Separator />

          {/* Dropdown list */}
          {filteredItems.length > 0 ? (
            filteredItems.map((item) => (
              <SelectItem key={item} value={item}>
                {item}
              </SelectItem>
            ))
          ) : (
            <div className="pt-2 py-2 text-sm text-muted-foreground">
              {noMatchFallback}
            </div>
          )}
        </SelectContent>
      </Select>

      {/* Error message */}
      {errorMessage && (
        <p className="text-sm text-destructive pt-1">{errorMessage}</p>
      )}
    </div>
  );
}

/* -------------------- Exports -------------------- */
// Exporting the ComboBox component
export { ComboBox };
