"use client";

/* -------------------- Imports -------------------- */
import * as React from "react";
import { cn } from "@/lib/utils";
import { BarLoader } from "react-spinners";

/* -------------------- ScrollArea Component -------------------- */
// Custom scrollable container with synced scrollbar thumb
function ScrollArea({
  className,
  children,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) {
  const viewportRef = React.useRef<HTMLDivElement>(null);
  const thumbRef = React.useRef<HTMLDivElement>(null);

  // Sync scrollbar thumb size & position
  React.useEffect(() => {
    const viewport = viewportRef.current;
    const thumb = thumbRef.current;
    if (!viewport || !thumb) return;

    const updateThumb = () => {
      const { scrollTop, scrollHeight, clientHeight } = viewport;
      const thumbHeight = (clientHeight / scrollHeight) * clientHeight;
      const top = (scrollTop / scrollHeight) * clientHeight;

      thumb.style.height = `${thumbHeight}px`;
      thumb.style.transform = `translateY(${top}px)`;
    };

    updateThumb();
    viewport.addEventListener("scroll", updateThumb);
    window.addEventListener("resize", updateThumb);

    return () => {
      viewport.removeEventListener("scroll", updateThumb);
      window.removeEventListener("resize", updateThumb);
    };
  }, []);

  return (
    <div
      data-slot="scroll-area"
      className={cn("relative overflow-hidden", className)}
      {...props}
    >
      <div
        data-slot="scroll-area-viewport"
        ref={viewportRef}
        className={cn(
          "overflow-auto scrollbar-hide size-full rounded-[inherit] transition-[color,box-shadow] outline-none focus-visible:ring-[3px] focus-visible:outline-1 focus-visible:ring-ring/50"
        )}
      >
        {children}
      </div>

      <div
        data-slot="scroll-area-scrollbar"
        className="absolute top-0 right-0 h-full bg-transparent"
      >
        <div
          data-slot="scroll-area-thumb"
          ref={thumbRef}
          className="bg-border relative w-full rounded-full transition-colors"
        />
      </div>

      {/* Optional loader at bottom */}
      <BarLoader />
    </div>
  );
}

/* -------------------- Exports -------------------- */
export { ScrollArea };
