"use client";

/* -------------------- Imports -------------------- */
import * as React from "react";
import { cn } from "@/lib/utils";

/* -------------------- Props -------------------- */
interface SwitchProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  checked?: boolean; // Controlled checked state
  defaultChecked?: boolean; // Uncontrolled default state
  onCheckedChange?: (checked: boolean) => void; // Change handler
  trackColor?: string; // Custom track color class
}

/* -------------------- Switch Component -------------------- */
// A toggle switch supporting both controlled and uncontrolled usage
const Switch = React.forwardRef<HTMLButtonElement, SwitchProps>(
  (
    {
      className,
      checked: controlledChecked,
      defaultChecked = false,
      onCheckedChange,
      disabled,
      trackColor = "bg-primary",
      ...props
    },
    ref
  ) => {
    const isControlled = controlledChecked !== undefined;
    const [uncontrolledChecked, setChecked] = React.useState(defaultChecked);
    const isChecked = isControlled ? controlledChecked : uncontrolledChecked;

    const toggle = () => {
      if (disabled) return;
      if (!isControlled) setChecked(!isChecked);
      onCheckedChange?.(!isChecked);
    };

    return (
      <button
        type="button"
        role="switch"
        aria-checked={isChecked}
        disabled={disabled}
        ref={ref}
        onClick={toggle}
        className={cn(
          "peer inline-flex h-[20px] w-[32px] shrink-0 items-center rounded-full border border-transparent shadow-xs outline-none transition-all",
          "focus-visible:ring-[3px] focus-visible:ring-ring/50 focus-visible:border-ring",
          "disabled:cursor-not-allowed disabled:opacity-50",
          isChecked ? trackColor : "bg-input dark:bg-input/80",
          className
        )}
        {...props}
      >
        <span
          className={cn(
            "pointer-events-none block h-[14px] w-[14px] rounded-full ring-0 transition-transform",
            isChecked
              ? "translate-x-[calc(100%)] dark:bg-primary-foreground bg-background"
              : "translate-x-[2px] bg-background dark:bg-foreground"
          )}
        />
      </button>
    );
  }
);

Switch.displayName = "Switch";

/* -------------------- Exports -------------------- */
export { Switch };
