/* -------------------- Imports -------------------- */
import * as React from "react";
import { cn } from "@/lib/utils";

/* -------------------- Types -------------------- */
interface TextareaProps extends React.ComponentProps<"textarea"> {
  resizeVertical?: boolean; // Allow vertical resizing
  resizeHorizontal?: boolean; // Allow horizontal resizing
}

/* -------------------- Textarea -------------------- */
// Styled textarea with optional resize controls
function Textarea({
  className,
  resizeVertical,
  resizeHorizontal,
  ...props
}: TextareaProps) {
  let resizeClass = "resize-none";

  if (resizeVertical && resizeHorizontal) {
    resizeClass = "resize";
  } else if (resizeVertical) {
    resizeClass = "resize-y";
  } else if (resizeHorizontal) {
    resizeClass = "resize-x";
  }

  return (
    <textarea
      data-slot="textarea"
      className={cn(
        "placeholder:text-muted-foreground selection:bg-primary selection:text-primary-foreground dark:bg-input/30 border-input flex w-full min-w-0 rounded-md border bg-transparent p-[14px] py-[10px] text-base shadow-xs transition-[color,box-shadow] outline-none disabled:pointer-events-none disabled:cursor-not-allowed disabled:opacity-50 md:text-sm",
        "focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px]",
        "aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
        "min-h-16",
        resizeClass,
        className
      )}
      {...props}
    />
  );
}

/* -------------------- Exports -------------------- */
export { Textarea };
